<table-code>
<table-row-code title="Insert Insert"  
code="
 INSERT INTO users 
    (first_name, last_name, email, password, location, dept, is_admin, register_date) 
    values ('Brad', 'Traversy', 'brad@gmail.com', '123456','Massachusetts', 'development', 1, now());
