</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="in" tabindex="-1"><a class="header-anchor" href="#in"><span>IN</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">SELECT</span> <span class="token operator">*</span> <span class="token keyword">FROM</span> users <span class="token keyword">WHERE</span> dept <span class="token operator">IN</span> <span class="token punctuation">(</span><span class="token string">'design'</span><span class="token punctuation">,</span> <span class="token string">'sales'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="create-remove-index" tabindex="-1"><a class="header-anchor" href="#create-remove-index"><span>Create &amp; Remove Index</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">CREATE</span> <span class="token keyword">INDEX</span> LIndex <span class="token keyword">On</span> users<span class="token punctuation">(</span>location<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token keyword">DROP</span> <span class="token keyword">INDEX</span> LIndex <span class="token keyword">ON</span> users<span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="new-table-with-foreign-key-posts" tabindex="-1"><a class="header-anchor" href="#new-table-with-foreign-key-posts"><span>New Table With Foreign Key (Posts)</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">CREATE</span> <span class="token keyword">TABLE</span> posts<span class="token punctuation">(</span>
id <span class="token keyword">INT</span> <span class="token keyword">AUTO_INCREMENT</span><span class="token punctuation">,</span>
   user_id <span class="token keyword">INT</span><span class="token punctuation">,</span>
   title <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
   body <span class="token keyword">TEXT</span><span class="token punctuation">,</span>
   publish_date <span class="token keyword">DATETIME</span> <span class="token keyword">DEFAULT</span> <span class="token keyword">CURRENT_TIMESTAMP</span><span class="token punctuation">,</span>
   <span class="token keyword">PRIMARY</span> <span class="token keyword">KEY</span><span class="token punctuation">(</span>id<span class="token punctuation">)</span><span class="token punctuation">,</span>
   <span class="token keyword">FOREIGN</span> <span class="token keyword">KEY</span> <span class="token punctuation">(</span>user_id<span class="token punctuation">)</span> <span class="token keyword">REFERENCES</span> users<span class="token punctuation">(</span>id<span class="token punctuation">)</span>
<span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="multiple-tables" tabindex="-1"><a class="header-anchor" href="#multiple-tables"><span>Multiple Tables</span></a></h2>
<h3 id="add-data-to-posts-table" tabindex="-1"><a class="header-anchor" href="#add-data-to-posts-table"><span>Add Data to Posts Table</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">INSERT</span> <span class="token keyword">INTO</span> posts<span class="token punctuation">(</span>user_id<span class="token punctuation">,</span> title<span class="token punctuation">,</span> body<span class="token punctuation">)</span> <span class="token keyword">VALUES</span> <span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token string">'Post One'</span><span class="token punctuation">,</span> <span class="token string">'This is post one'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">3</span><span class="token punctuation">,</span> <span class="token string">'Post Two'</span><span class="token punctuation">,</span> <span class="token string">'This is post two'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token string">'Post Three'</span><span class="token punctuation">,</span> <span class="token string">'This is post three'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token string">'Post Four'</span><span class="token punctuation">,</span> <span class="token string">'This is post four'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">5</span><span class="token punctuation">,</span> <span class="token string">'Post Five'</span><span class="token punctuation">,</span> <span class="token string">'This is post five'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">4</span><span class="token punctuation">,</span> <span class="token string">'Post Six'</span><span class="token punctuation">,</span> <span class="token string">'This is post six'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token string">'Post Seven'</span><span class="token punctuation">,</span> <span class="token string">'This is post seven'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token string">'Post Eight'</span><span class="token punctuation">,</span> <span class="token string">'This is post eight'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">3</span><span class="token punctuation">,</span> <span class="token string">'Post Nine'</span><span class="token punctuation">,</span> <span class="token string">'This is post none'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">4</span><span class="token punctuation">,</span> <span class="token string">'Post Ten'</span><span class="token punctuation">,</span> <span class="token string">'This is post ten'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="inner-join" tabindex="-1"><a class="header-anchor" href="#inner-join"><span>INNER JOIN</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">SELECT</span>
  users<span class="token punctuation">.</span>first_name<span class="token punctuation">,</span>
  users<span class="token punctuation">.</span>last_name<span class="token punctuation">,</span>
  posts<span class="token punctuation">.</span>title<span class="token punctuation">,</span>
  posts<span class="token punctuation">.</span>publish_date
<span class="token keyword">FROM</span> users
<span class="token keyword">INNER</span> <span class="token keyword">JOIN</span> posts
<span class="token keyword">ON</span> users<span class="token punctuation">.</span>id <span class="token operator">=</span> posts<span class="token punctuation">.</span>user_id
<span class="token keyword">ORDER</span> <span class="token keyword">BY</span> posts<span class="token punctuation">.</span>title<span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="new-table-with-2-foriegn-keys" tabindex="-1"><a class="header-anchor" href="#new-table-with-2-foriegn-keys"><span>New Table With 2 Foriegn Keys</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">CREATE</span> <span class="token keyword">TABLE</span> comments<span class="token punctuation">(</span>
	id <span class="token keyword">INT</span> <span class="token keyword">AUTO_INCREMENT</span><span class="token punctuation">,</span>
    post_id <span class="token keyword">INT</span><span class="token punctuation">,</span>
    user_id <span class="token keyword">INT</span><span class="token punctuation">,</span>
    body <span class="token keyword">TEXT</span><span class="token punctuation">,</span>
    publish_date <span class="token keyword">DATETIME</span> <span class="token keyword">DEFAULT</span> <span class="token keyword">CURRENT_TIMESTAMP</span><span class="token punctuation">,</span>
    <span class="token keyword">PRIMARY</span> <span class="token keyword">KEY</span><span class="token punctuation">(</span>id<span class="token punctuation">)</span><span class="token punctuation">,</span>
    <span class="token keyword">FOREIGN</span> <span class="token keyword">KEY</span><span class="token punctuation">(</span>user_id<span class="token punctuation">)</span> <span class="token keyword">references</span> users<span class="token punctuation">(</span>id<span class="token punctuation">)</span><span class="token punctuation">,</span>
    <span class="token keyword">FOREIGN</span> <span class="token keyword">KEY</span><span class="token punctuation">(</span>post_id<span class="token punctuation">)</span> <span class="token keyword">references</span> posts<span class="token punctuation">(</span>id<span class="token punctuation">)</span>
<span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="add-data-to-comments-table" tabindex="-1"><a class="header-anchor" href="#add-data-to-comments-table"><span>Add Data to Comments Table</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">INSERT</span> <span class="token keyword">INTO</span> comments<span class="token punctuation">(</span>post_id<span class="token punctuation">,</span> user_id<span class="token punctuation">,</span> body<span class="token punctuation">)</span> <span class="token keyword">VALUES</span> <span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">3</span><span class="token punctuation">,</span> <span class="token string">'This is comment one'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">,</span> <span class="token string">'This is comment two'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">5</span><span class="token punctuation">,</span> <span class="token number">3</span><span class="token punctuation">,</span> <span class="token string">'This is comment three'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token number">4</span><span class="token punctuation">,</span> <span class="token string">'This is comment four'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">,</span> <span class="token string">'This is comment five'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">3</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">,</span> <span class="token string">'This is comment six'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">3</span><span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">,</span> <span class="token string">'This is comment six'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">5</span><span class="token punctuation">,</span> <span class="token number">4</span><span class="token punctuation">,</span> <span class="token string">'This is comment seven'</span><span class="token punctuation">)</span><span class="token punctuation">,</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> <span class="token number">3</span><span class="token punctuation">,</span> <span class="token string">'This is comment seven'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="left-join" tabindex="-1"><a class="header-anchor" href="#left-join"><span>Left Join</span></a></h3>
<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="token keyword">SELECT</span>
comments<span class="token punctuation">.</span>body<span class="token punctuation">,</span>
posts<span class="token punctuation">.</span>title
<span class="token keyword">FROM</span> comments
<span class="token keyword">LEFT</span> <span class="token keyword">JOIN</span> posts <span class="token keyword">ON</span> posts<span class="token punctuation">.</span>id <span class="token operator">=</span> comments<span class="token punctuation">.</span>post_id
<span class="token keyword">ORDER</span> <span class="token keyword">BY</span> posts<span class="token punctuation">.</span>title<span class="token punctuation">;</span>

</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="join-multiple-tables" tabindex="-1"><a class="header-anchor" href="#join-multiple-tables"><span>Join Multiple Tables</span></a></h3>
